import React from 'react';
import parse from 'html-react-parser';
import { useRouter } from 'next/router';

export default function Admissions() {
	const router = useRouter();
	const getData = () => {
		switch (router.asPath) {
			case '/admission/admission-it':
				return dataAdmissions;
			case '/about/faculty':
				return dataAbout;
			case '/educate/educate-special-program':
				return dataAdmissionSpecial;
			case '/admission/admission-special':
				return dataAdmissionSpecial;
				case '/educate/educate-program':
					return dataEducateProgram;
			case '/educate/educate-after-program':
				return dataEducateAfter;
			default:
				typeof window !== 'undefined' && router.push('/not-found');
		}
	};

	return (
		<div className='container'>
			{/* Render HTML */}
			<div className='admissions'>
				{getData()?.content && parse(getData()?.content)}
			</div>
		</div>
	);
}

const dataAdmissions = {
	_id: 'ObjectId',
	typeCode: 'admission',
	typeContent: 'content',
	link: null,
	title: 'Tuyển sinh',
	slug: 'tuyen-sinh',
	banner: 'https://baner.png',
	description: '',
	content:
		'<p>Ứng dụng của công nghệ thông tin trong đời sống xã hội hiện đại ngày càng nhiều, từ thương mại đến giải trí và thậm chí cả y tế, văn hóa, xã hội và giáo dục,… Công nghệ thông tin được xem như là ngành mũi nhọn hướng đến sự phát triển của khoa học và công nghệ trong thời đại số. Đây cũng là một trong những ngành có xu hướng thay đổi theo từng ngày. Vì vậy, Kỹ sư tốt nghiệp ngành công nghệ thông tin có nhiều cơ hội để phát triển nghề nghiệp. Đặc biệt là những công ty phần mềm lớn có vốn đầu tư nước ngoài hoặc làm việc ở nước ngoài, vì vốn kiến thức bạn được học có thể sử dụng ở bất cứ đâu trên Thế giới.</p><p>&nbsp;</p><figure class="image"><img src="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NG%C3%80NHA-06-2-scaled.jpg" alt="" srcset="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NGÀNHA-06-2-scaled.jpg 1440w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NGÀNHA-06-2-300x169.jpg 300w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NGÀNHA-06-2-1024x576.jpg 1024w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NGÀNHA-06-2-768x432.jpg 768w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NGÀNHA-06-2-1536x864.jpg 1536w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NGÀNHA-06-2-696x392.jpg 696w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NGÀNHA-06-2-1068x601.jpg 1068w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/BANNER-32-NGÀNHA-06-2-747x420.jpg 747w" sizes="100vw" width="1440"><figcaption>Sinh viên ngành Công nghệ Thông tin tại phòng máy trong giờ học thực hành.</figcaption></figure><h3>&nbsp;</h3><h3>Mã ngành: 7480201</h3><p><strong>Thời gian đào tạo: 4 năm</strong><br><strong>Văn bằng: &nbsp;Cử nhân Công nghệ Thông tin</strong><br><strong>Tổ hợp môn xét tuyển:</strong></p><ul><li>A00: Toán – Lý – Hóa</li><li>A01: Toán – Lý – Tiếng Anh</li><li>D01: Toán – Văn – Tiếng Anh</li><li>D10: Toán – Địa – Tiếng Anh</li></ul><h3><strong>Học ngành Công nghệ Thông tin có gì thú vị?</strong></h3><p>Công nghệ Thông tin (IT – Information Technology) là một thuật ngữ bao gồm mạng lưới internet, phần mềm, trao đổi, phân phối, lưu trữ và sử dụng dữ liệu, thông tin dưới mọi hình thức khác nhau.</p><p>Đối với thế hệ Z- thế hệ sử dụng công nghệ thông tin từ rất sớm thì công nghệ thông tin là một phần thiết yếu trong cuộc sống. Vì thế nếu bạn yêu công nghệ, thích máy tính, đam mê lập trình, luôn tìm tòi cải tiến những thiết bị/ ứng dụng xung quanh mình thì chọn Công nghệ thông tin là bạn đã đi đúng đường rồi đấy!</p><h3><strong>Bạn cần tố chất nào để học ngành Công nghệ Thông tin?</strong></h3><ul><li>Đam mê công nghệ</li><li>Tư duy logic</li><li>Năng động, sáng tạo, tinh thần tự học kiến thức mới</li><li>Kỹ năng làm việc nhóm</li></ul><h3><strong>Học ngành Công nghệ Thông tin ở đâu?</strong></h3><p>Tại khu vực Tp. HCM có khá nhiều trường đại học đào tạo ngành Công nghệ Thông tin uy tín: Trường Đại học Văn Lang, Trường Đại học Công nghệ thông tin – Đại học Quốc gia Tp.HCM, Trường Đại học Bách Khoa Tp.HCM, Trường Đại học Khoa học Tự Nhiên,…</p><p>Các bạn cần tìm hiểu đề án tuyển sinh của mỗi trường, phương thức xét tuyển và tham khảo điểm trúng tuyến các năm gần đây để xét tuyển vào trường đại học phù hợp nhất với kết quả học tập và thi THPT.</p><p>&nbsp;</p><p><strong>KHI BẠN LÀ SINH VIÊN CÔNG NGHỆ THÔNG TIN TẠI VĂN LANG</strong></p><p>Trường Đại học Văn Lang mở ngành Công nghệ Thông tin từ năm 1995. Ngành Công nghệ Thông tin của VLU có 4 chuyên ngành: Công nghệ phần mềm, Công nghệ dữ liệu, Tin học Quản trị và An ninh mạng. Từ năm 2001, Văn Lang tham gia vào hệ thống đào tạo kỹ sư công nghệ thông tin theo chuẩn Nhật Bản.</p><p>Sinh viên ngành Công thông Thông tin Văn Lang luôn chủ động học hỏi và tìm kiếm những cơ hội trải nghiệm cho bản thân thông qua các hoạt động của Câu lạc bộ, tham quan thực tế các công ty chuyên về IT nhằm cập nhật xu hướng và tích kinh nghiệm của ngành IT.</p><p>&nbsp;</p><h3><strong>Có bao nhiêu lựa chọn khi học ngành Công nghệ Thông tin&nbsp;tại Trường Đại học Văn Lang?</strong></h3><p><strong>Sinh viên có 02 lựa chọn khi học ngành Công nghệ Thông tin tại Văn Lang:</strong></p><ul><li>Chương trình tiêu chuẩn.</li><li>Chương trình đào tạo đặc biệt.<br>&nbsp;</li></ul><p><strong>1. CHƯƠNG TRÌNH TIÊU CHUẨN (uy tín trong 25 năm qua)</strong></p><p>Chương trình đào tạo luôn được chú trọng công tác thực hành thông qua liên kết với doanh nghiệp để nâng cao chất lượng đào tạo, có phương án đào tạo trên giảng đường kết hợp với thực hành trong các phòng Lab, các doanh nghiệp, trên môi trường mạng. Nhờ vậy sinh viên được trang bị các kỹ năng cần thiết để làm việc trong thực tế và nâng cao trình độ ngoại ngữ.</p><p>&nbsp;</p><p>Công nghệ dữ liệu là một trong những thế mạnh và khác biệt trong chương trình đào tạo Công nghệ Thông tin ở Văn Lang. Chương trình đào tạo được thiết kế đổi mới về môn học, phương pháp và định hướng nghề nghiệp.</p><p>&nbsp;</p><figure class="image"><img src="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-a.jpg" alt="" srcset="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-a.jpg 1000w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-a-300x203.jpg 300w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-a-768x521.jpg 768w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-a-696x472.jpg 696w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-a-619x420.jpg 619w" sizes="100vw" width="1000"><figcaption>E-Solution team, Khoa Công nghệ Thông tin đạt giải Ba cuộc thi Ý tưởng sáng tạo sinh viên Văn Lang mùa 2 năm 2020 với đề tài VL E-llearning.</figcaption></figure><p>&nbsp;</p><p>Chương trình học được thực hiện trong 4 năm học (8 học kỳ chính và 3 học kỳ hè). Ngành Công nghệ Thông tin đào tạo cho người học: Các kiến thức về xã hội, toàn cầu hóa, đạo đức nghề nghiệp và các kiến thức chuyên môn.</p><ul><li>Hai năm đầu, sinh viên được đào tạo khối kiến thức đại cương và cơ sở ngành gắn với định hướng ứng dụng Công nghệ thông tin, sớm tiếp cận các hện thông CPS, sớm làm viên với các mô hình công nghệ 4.0, phát triển kỹ năng lập trình phần mềm, xử lý dữ liệu số…</li><li>Các môn học được cập nhật mới: xử lý dữ liệu lớn, trí tuệ nhân tạo, học máy, số hóa, quản trị thông tin số.</li></ul><p>Nhà trường chú trọng vào việc rèn luyện các kỹ năng mềm như Kỹ năng thuyết trình, giao tiếp, kỹ năng làm việc nhóm cho sinh viên để đáp ứng nhu cầu thực tế.</p><p>Để tra cứu về chương trình học tập, khối lượng kiến thức của khóa học mà bạn cần tích lũy và tra cứu các thông tin về học phần bạn có thể tham khảo<strong> </strong><a href="http://www.vanlanguni.edu.vn/images/AttachFile/so-tay-hoc-tap-2019/K25T-IT.pdf"><strong>tại đây.</strong></a></p><p>&nbsp;</p><p><strong>2. CHƯƠNG TRÌNH ĐÀO TẠO ĐẶC BIỆT (tăng cường trải nghiệm, tiếp cận chuẩn công dân toàn cầu)</strong></p><p><strong>KHI BẠN LÀ SINH VIÊN CHUƠNG TRÌNH ĐÀO TẠO ĐẶC BIỆT</strong></p><p>Chương trình Đào tạo Đặc biệt được thiết kế dựa trên nền tảng Triết lý Giáo dục Khai phóng, cung cấp cho người học kỹ năng và tư duy khám phá, định vị bản thân, thích ứng với tiêu chuẩn mới của bối cảnh hội nhập toàn cầu.</p><ol><li><strong>Trải nghiệm thực tế (Real World Experience)</strong></li></ol><p>Quy mô lớp học của Chương trình Đào tạo đặc biệt tối đa 40 sinh viên giúp sinh viên chủ động và phát huy tối đa năng lực học tập. Theo thiết kế hiện nay, khoảng 50% – 70% chương trình sẽ đào tạo bằng tiếng Anh và hơn 50% chương trình gắn liền với các hoạt động thực tiễn tại doanh nghiệp. Sinh viên cũng có thể khởi nghiệp từ chính giảng đường đại học với sự hỗ trợ của nhà trường, các đối tác và doanh nhân thành đạt.</p><p>Sinh viên năm cuối được trải nghiệm: Học kỳ doanh nghiệp, Dự án doanh nghiệp (Social Business Project, Startup Incubation Program), Chương trình trao đổi sinh viên quốc tế,…</p><ol><li><strong>Công dân toàn cầu (Global Citizen)</strong></li></ol><p>Vào năm nhất, sinh viên được đầu tư đổ nền tiếng Anh tương ứng với trình độ IELTS 5.5, đảm bảo cho việc học tập chuyên ngành từ 50-70% tiếng Anh. Trình độ Anh văn đạt chuẩn IELTS 6.0, giúp sinh viên giao tiếp và làm việc bằng tiếng Anh thành thạo.</p><p>Cá nhân hóa và tối ưu hóa chương trình đào tạo: Sinh viên có thể chọn học (không bắt buộc) một combo môn học của ngành phụ (khối lượng học thêm tương đương 15 tín chỉ) để bổ trợ cho ngành học của mình, sau khi hoàn thảnh combo ngành phụ sinh viên sẽ được cấp chứng nhận điểm của Trường Đại học Văn Lang. Sinh viên có thể chọn combo môn học thuộc 01 trong 08 ngành phụ: Quan hệ Công chúng, Marketing, Kế toán, Tâm lý học, Thương mại Quốc tế, Ngôn ngữ Anh, Quản trị Khách sạn – Du lịch.</p><p>&nbsp;</p><ol><li><strong>Ứng dụng Công nghệ (Smart University)</strong></li></ol><ul><li>Được tiên phong áp dụng công nghệ trong giảng dạy: thực tế ảo, Trí tuệ nhân tạo, 3D,..</li><li>Hệ thống học trực tuyến, thư viện trực tuyến</li><li>App ID, dịch vụ sinh viên được số hóa (sẽ phát triển đồng bộ cùng quá trình Đại học Văn Lang chuyển đổi số).</li></ul><p><strong>Chương trình Đào tạo Đặc biệt ngành Công nghệ Thông tin được phát triển từ Chương trình Tiêu chuẩn với chất lượng đào tạo đã được khẳng định trong nhiều năm qua, và kết hợp với các điểm đặc trưng:</strong></p><p><strong>Chú trọng thực hành</strong></p><p>Sinh viên CTĐT Đặc biệt ngành Công nghệ Thông tin được học song song lý thuyết và thực hành tại phòng Lab (không quá 30 sinh viên).</p><p><strong>Tiếp cận doanh nghiệp sớm</strong></p><p>Từ năm 3, sinh viên CTĐTĐB ngành Công nghệ Thông tin được đưa đến các doanh nghiệp thực tập và được doanh nghiệp đào tạo chuyên ngành.</p><p><strong>Định hướng chuyên sâu</strong></p><p>Năm 4, Khoa sẽ cung cấp các doanh nghiệp đã thỏa thuận hợp tác, sinh viên CTĐT Đặc biệt ngành Công nghệ Thông tin sẽ được tự do lựa chọn doanh nghiệp ở lĩnh vực chuyên môn mà sinh viên mong muốn làm việc sau này.</p><p>Có 4 chuyên ngành: Công nghệ Phần mềm, Công nghệ Dữ liệu, Tin học Quản trị, An ninh mạng. Đồ án tốt nghiệp chính là nhu cầu thực tại cuả Doanh nghiệp mà SV đã lựa chọn.</p><p>&nbsp;</p><figure class="image"><img src="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-digital-citizen-1.jpg" alt="" srcset="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-digital-citizen-1.jpg 1000w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-digital-citizen-1-300x200.jpg 300w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-digital-citizen-1-768x513.jpg 768w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-digital-citizen-1-696x465.jpg 696w" sizes="100vw" width="1000"><figcaption>Hội thảo “Thành phố thông minh và Nền tảng dịch vụ số” dành riêng cho sinh viên CTĐT Đặc biệt, với tham gia của ThS. Bùi Minh Phụng – Phó trưởng khoa Công nghệ thông tin và diễn giả Lê Hùng (CEO Công ty NamiQ AI &amp; CX Transformation)</figcaption></figure><h3>&nbsp;</h3><h3><strong>Hoạt động của sinh viên ngành Công nghệ Thông tin tại Văn Lang</strong></h3><p>Câu lạc bộ <strong>Van Lang Tech</strong>&nbsp;là nơi các bạn yêu công nghệ giao lưu, học hỏi, chia sẻ với nhau những kiến thức chuyên môn. Đây cũng là sân chơi bổ ích để các bạn thường xuyên cập nhật xu hướng công nghệ mới trong và ngoài nước.</p><p>&nbsp;</p><figure class="image"><img src="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-scaled.jpg" alt="" srcset="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-scaled.jpg 1440w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-300x210.jpg 300w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-1024x716.jpg 1024w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-768x537.jpg 768w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-1536x1074.jpg 1536w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-696x486.jpg 696w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-1068x746.jpg 1068w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-601x420.jpg 601w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-c-100x70.jpg 100w" sizes="100vw" width="1440"><figcaption>Đêm nhạc hội ITSM là lời cảm ơn chân thành, tri ân đến các thầy cô nhân dịp 20/11; đồng thời thể hiện sự đoàn kết, chia sẻ, hợp tác của các cựu sinh viên Văn Lang đến các đàn em của mình (Ảnh: Van Lang Photographers).</figcaption></figure><p>&nbsp;</p><p>Mọi người thường nghĩ sinh viên khối ngành công nghệ thường khá khô khan, tuy nhiên sinh viên Văn Lang thì luôn máu lửa, sôi nổi và đầy nhiệt huyết. Hằng năm, sinh viên liên kết 3 Khoa gồm: Khoa Công nghệ thông tin, Khoa Công nghệ và Khoa Kỹ thuật luôn có một chương trình truyền thống mang tên<strong> ITSM </strong>nằm trong chuỗi hoạt động chào mừng ngày Nhà giáo Việt Nam 20/11 của Trường Đại học Văn Lang.</p><h3><strong>Cơ hội việc làm với sinh viên học ngành Công nghệ Thông tin?</strong></h3><p>Cử nhân Công nghệ Thông tin có nhiều cơ hội lựa chọn công việc ở các doanh nghiệp, công ty chuyên ngành IT ở các vị trí như:</p><ul><li>Kỹ sư dữ liệu</li><li>Lập trình viên phát triển phần mềm</li><li>Lập trình viên IoT</li><li>Quản trị Mạng</li><li>Thiết kế hệ thống Mạng và bảo trì hệ thống Mạng.</li><li>&nbsp;</li></ul><figure class="image"><img src="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-scaled.jpg" alt="" srcset="https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-scaled.jpg 1440w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-300x213.jpg 300w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-1024x728.jpg 1024w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-768x546.jpg 768w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-1536x1091.jpg 1536w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-2048x1455.jpg 2048w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-696x494.jpg 696w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-1068x759.jpg 1068w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-1920x1364.jpg 1920w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-591x420.jpg 591w, https://tuyensinh.vanlanguni.edu.vn/wp-content/uploads/2020/02/vlu-cntt-b-100x70.jpg 100w" sizes="100vw" width="1440"><figcaption>Sinh viên ngành Công nghệ Thông tin năng động tham gia các buổi hướng nghiệp cho học sinh với chủ đề “IT – Kết nối tương lai”.</figcaption></figure><h3>&nbsp;</h3><h3><strong>Nhu cầu thị trường, xu hướng việc làm với Kỹ sư&nbsp;Công nghệ Thông tin?</strong></h3><p>Chọn học ngành công nghệ thông tin bạn sẽ có nhiều cơ hội việc làm ở nhiều ngành nghề khác nhau, nên từ đó Kỹ sư Công nghệ Thông tin cũng có nhiều lựa chọn để phát triển nghề nghiệp hơn. Công việc IT còn cho phép bạn lựa chọn làm việc toàn thời gian, bán thời gian hoặc làm việc tự do.</p><p>Trong giai đoạn giới công nghệ có nhiều thay đổi trước làn sóng chuyển đổi số của các doanh nghiệp truyền thống và các quỹ đầu tư nước ngoài vào Việt Nam ngày càng nhiều hơn, việc tăng mạnh nhu cầu nguồn nhân lực IT là điều tất yếu.</p><p>Theo khảo sát của TopDev, một trong những công ty hàng đầu về tuyển dụng nhân sự ngành IT cho thấy năm 2019 nhân sự ngành IT thiếu hụt từ 70.000-90.000 nhân sự, dự đoán năm 2020 thiếu hụt khoảng 100.000 và năm 2021 thiếu khoảng 190.000 nhân sự. Đây là cơ hội việc làm cho các tân kỹ sư ngành công nghệ thông tin trong tương lai.</p><h3><strong>Bao nhiêu điểm có thể trúng tuyển ngành Công nghệ Thông tin tại Văn Lang?</strong></h3><p>Bạn có thể tham khảo điểm mức điểm trúng tuyển ngành Công nghệ Thông tin các năm dưới đây:</p><ul><li>Điểm trúng tuyển theo phương thức xét điểm thi tốt nghiệp THPT: &nbsp;<strong>15.00</strong> điểm (2019), <strong>18</strong> điểm (2020).</li><li>Điểm trúng tuyển theo phương thức xét kết quả học tập THPT (học bạ): <strong>18.00</strong> điểm (2019), <strong>18.00</strong> điểm (2020)</li></ul><p><i><strong>Xem thêm:</strong></i> <i>Thông tin tuyển sinh năm 2021</i></p><p><strong>KHOA CÔNG NGHỆ THÔNG TIN</strong></p><ul><li>Trưởng Khoa: <strong>TS. Hoàng Lê Minh</strong></li><li>Phó trưởng Khoa: <strong>ThS. Bùi Minh Phụng</strong></li><li>Văn phòng Khoa: <strong>Lầu 5, tòa nhà A – Cơ sở 3</strong></li><li>Điện thoại: <strong>028. 71099240</strong> – ext: 4010 (CTSV), 4111 (Ban lãnh đạo Khoa)</li><li>Email:&nbsp;<a href="mailto:k.cntt@vanlanguni.edu.vn"><strong>k.cntt@vanlanguni.edu.vn</strong></a></li></ul>'
};

